mapper.select:
select c.*, u.mg_auth, u.user_regDate from Company c left join User u on c.com_num = u.com_num
where u.mg_auth = "2"
where u.mg_auth = "3"
where u.mg_auth = "5"
order by c.com_num desc limit #{cri.pageStart}, #{cri.amount}
order by c.com_num asc limit #{cri.pageStart}, #{cri.amount}

mapper.update:
update User
set mg_auth = #{mg_auth}
where com_num = #{com_num}
