mapper.select:
select user_pw
from User
where user_id = #{user_id}

mapper.update:
update QnA
set qa_title   = #{qa_title},
    qa_content = #{qa_content}
where qa_num = #{qa_num}

mapper.select:
select * from jobpostinglist j left join company c on j.com_num = c.com_num where 1 = 1


    and jpl_locationSi in

        #{jpl_locationSi}




    and jpl_locationGu in

        #{jpl_locationGu}




    and jpl_duty in

        #{jpl_duty}




    and jpl_workHistory in

        #{jpl_workHistory}




    and jpl_education in

        #{jpl_education}




    and jpl_salary in

        #{jpl_salary}




    and jpl_certificate in

        #{jpl_certificate}




    and jpl_conditions in

        #{jpl_conditions}




    and jpl_workForm in

        #{jpl_workForm}




    and jpl_title like
    concat('%', #{inputSearch}, '%') or
    com_name like
    concat('%', #{inputSearch}, '%')
